import './Prodotti.css';
import viaggi from './../Immagini/aereo.png';
import mondo from './../Immagini/Mondo.png';
import tappa from './../Immagini/Tappa.png';
import { Responsive, WidthProvider } from "react-grid-layout";
import styled from "styled-components";
const ResponsiveGridLayout = WidthProvider(Responsive);
const layout = [
    { i: "Viaggi", x: 0, y: 0, w: 1, h: 1 },
    { i: "Mete", x: 1, y: 0, w: 1, h: 1 },
    { i: "kuriboh", x: 2, y: 0, w: 1, h: 1 },
    { i: "spell-caster", x: 3, y: 0, w: 1, h: 1 },
    { i: "summoned-skull", x: 4, y: 0, w: 1, h: 1 }
  ];

  const GridItemWrapper = styled.div`
    back-ground-color: rgb(0, 1, 36);

  `;
  const getLayouts = () => {
    const savedLayouts = localStorage.getItem("grid-layout");

    return savedLayouts ? JSON.parse(savedLayouts) : { lg: layout };
  };
  const GridItemContent = styled.div`
    padding: 8px;
    color: white;
    background-color: purple;
    text-align: center;
  `;

  const Root = styled.div`

  `;
const Prodotti = () => {
  const handleLayoutChange = (layout, layouts) => {
    localStorage.setItem("grid-layout", JSON.stringify(layouts));
  };
    return (
      <Root id='container'>
        <ResponsiveGridLayout 
        layouts={getLayouts()}
      breakpoints={{ lg: 1200, md: 996, sm: 768, xs: 480, xxs: 0 }}
      cols={{ lg: 5, md: 4, sm: 3, xs: 2, xxs: 1 }}
      rowHeight={300}
      width={1000}
      onLayoutChange={handleLayoutChange}
        >
          <GridItemWrapper draggable={false} key="Viaggi">
            <GridItemContent draggable={false}><img id='immagine' src={viaggi}/>Viaggi</GridItemContent>
          </GridItemWrapper>
          <GridItemWrapper draggable={false} key="Mete">
            <GridItemContent><img id='immagine' src={mondo}/>Mete</GridItemContent>
          </GridItemWrapper>
          <GridItemWrapper draggable={false} key="Tappe">
            <GridItemContent>Kuriboh</GridItemContent>
          </GridItemWrapper>
          <GridItemWrapper key="spell-caster">
            <GridItemContent>Spell Caster</GridItemContent>
          </GridItemWrapper>
          <GridItemWrapper key="summoned-skull">
            <GridItemContent>Summoned Skull</GridItemContent>
          </GridItemWrapper>
        </ResponsiveGridLayout>
      </Root>
    );
  };
export default Prodotti
