import './Prodotti.css';
import viaggi from './../Immagini/aereo.png'
import mondo from './../Immagini/Mondo.png'
import tappa from './../Immagini/Tappa.png'
import React from 'react';
import { View, StyleSheet, Text } from 'react-native';
import GridImageViewerCaption from 'react-native-grid-image-viewer-with-caption';
export default class App extends Component {
    constructor(props) {
      super(props);
      this.state = {
        gallery:[
          { image: './../Immagini/aereo.png', text:'Caption 1' },
          { image: 'http://image.jpg', text:'Caption 2' },
          { image: 'http://image.jpg', text:'Caption 3' }
        ]
      };
    }
    render() {
        return (
           <View style={styles.background}>
          <Text style={styles.headline_text}>Grid View Images With Caption</Text>
          <Text style={styles.explore_text}>Click on an image to view in full screen mode</Text>

          <GridImageViewerCaption data={this.state.gallery} captionColor="#fff" />
        </View>
        );
      }
    };
    const styles = StyleSheet.create({
        background: {
          backgroundColor: 'black',
          flex: 1
        },
        headline_text: {
          color: 'white',
          fontSize: 30,
          fontWeight: 'bold',
          marginTop: 50,
          marginLeft: 20
        },
        explore_text: {
          marginTop: 5,
          marginBottom: 10,
          color: 'white',
          marginLeft: 20,
          fontSize: 12,
          fontWeight: '600'
        },
      });
const Prodotti = () =>{
    return(
        <div id='container'>
            <h2 id='divisore'>Prodotti</h2>
            <div id='ImPro'>
                <img id='immagine' src={viaggi} alt="Immagine 1"/>
                <img id='immagine' src={mondo} alt="Immagine 2"/>
                <img id='immagine' src={tappa} alt="Immagine 3"/>
            </div>
        </div>
    );
}
